mapper.select:
select no, title, contents, nickname, date_format(writedate,'%Y-%m-%d') as writedate, hit from board where category='exercise'
and nickname=#{param2}
and ${param1.searchKey} like '%${param1.searchWord}%'
order by no desc limit ${param1.onePageRecord} offset ${param1.offsetIndex}

select count(no) cnt from board where category='exercise'
and nickname=#{param2}
and ${param1.searchKey} like '%${param1.searchWord}%'

select count(nickname) as
cnt from exercise_member
where nickname=#{param1} and no=#{param2}

select no,
exerciseNo, nickname, status
from exercise_member where exerciseNo=#{param1}

select no, title, hashtag, contents, date_format(startdate, '%Y-%m-%d')as startdate, date_format(enddate, '%Y-%m-%d')as enddate, date_format(writedate, '%Y-%m-%d') as writedate, placeinfo, applicantCnt, applicantMax, hit, nickname, img from everyexercise
where ${searchKey} like '%${searchWord}%'
order by no desc limit ${onePageRecord} offset ${offsetIndex}

mapper.update:
update everyexercise set title=#{title}, hashtag=#{hashtag},
contents=#{contents},startdate= DATE_FORMAT(startdate, '%Y-%m-%d %H:%i:%S') ,  enddate=DATE_FORMAT(enddate, '%Y-%m-%d %H:%i:%S') ,writedate =now(),	 

 placeinfo=#{placeinfo},

 applicantMax=#{applicantMax}

 ,img=#{img}

where no=#{no} and nickname=#{nickname}
