mapper.select:
select no, email, nickname, tel, profile, birthdate, gender, age, height, weight, active , bmr, amr, verify
from member where email=#{email}

	 and pwd=#{pwd}

select no, email, nickname, tel, profile, birthdate, gender, age, height, weight, active , bmr, amr, verify, recentvisit, joindate from member
WHERE ${searchKey} like '%${searchWord}%'
ORDER BY no DESC LIMIT ${onePageRecord} OFFSET ${offsetIndex}

mapper.update:
update member set email=#{email}, nickname=#{nickname}, tel=#{tel}, birthdate=#{birthdate}, age=#{age}, height=#{height}, weight=#{weight}, active=#{active}
, verify=#{verify},  pwd=#{pwd},  BMR=#{BMR},  AMR=#{AMR}
where nickname=#{nickname}

update member
set pwd=#{pwd}
where email=#{email}
